clear all#plot vector from origin function plotVectors(vecs, color)    v = vecs'    for i = 1 : length(v) #length is num of rows        x = cat(1, [0,0], v(i,:));        quiver(x(1),  #start x               x(3),  #start y               x(2),  #x direction               x(4),'Linewidth',2, color); #y direction        grid on         hold on    endend A = [ 2.9 -1.5 0.1 -1 2.1 -4 -2 2.2 0.2 2; 4 -0.9 0 -1 3 -5 -3.5 2.6 1 3.5];At = A';figure(1)subplot(2,2,1)plot(A(1,:), A(2,:), '*');C = cov(At,1);[U,D,V] = svd(C);subplot(2,2,2)plotVectors([U(1,2); U(1,1)], 'b')